mapper.select:
SELECT 
FROM discuss_post
WHERE status != 'DELETE'

    AND user_id = #{userId}

ORDER BY top_weight DESC, create_time DESC
LIMIT #{offset}, #{limit}

mapper.update:
UPDATE discuss_post
SET comment_count = #{commentCount}
WHERE post_id = #{postId}

UPDATE discuss_post
SET title = #{title}, content = #{content}, first_image_url = #{firstImageUrl}
WHERE post_id = #{postId}
